import React, { useEffect, useState } from 'react';
import {
  Box,
  Container,
  Button,
  Grid,
  Paper,
  Typography,
  TextField,
  MenuItem,
  FormControl,
  FormLabel,
  FormControlLabel,
  RadioGroup,
  Radio,
} from '@mui/material';
import Graph from './Graph';

import { makeStyles } from '@material-ui/core';

import Message from './Message';

const OutputForm = ({
  weightChange,
  setWeightChange,
  submitted,
  BMR,
  BMRAdjusted,
  BMRGoal,
  protien,
  carbs,
  fat,
  macroBreakdown,
  setMacroBreakdown,
  calcMacros,
}) => {
  const [open, setOpen] = React.useState(false);
  const [modalContext, setModalContext] = React.useState('');

  const handleOpen = (context) => {
    setModalContext(context);
  };
  const handleClose = () => setOpen(false);

  useEffect(() => {
    if (modalContext !== '') {
      setOpen(true);
    }
  }, [modalContext]);

  return (
    <Box>
      <Message
        open={open}
        setOpen={setOpen}
        handleOpen={handleOpen}
        handleClose={handleClose}
        modalContext={modalContext}
        setMacroBreakdown={setMacroBreakdown}
        calcMacros={calcMacros}
        BMRGoal={BMRGoal}
        setWeightChange={setWeightChange}
      />

      {submitted && (
        <Box>
          <Box
            component="form"
            sx={{
              '& > :not(style)': { m: 1, width: '40ch' },
            }}
            noValidate
            autoComplete="off"
            style={{ paddingTop: '20px' }}
          >
            <TextField
              id="bmr"
              label="Basal Metabolic Rate (BMR)"
              variant="outlined"
              value={`${BMR} cal`}
              InputProps={{
                readOnly: true,
              }}
            />
            <TextField
              id="bmr-adjusted"
              label="BMR Adjusted"
              variant="outlined"
              value={`${BMRAdjusted} cal`}
              InputProps={{
                readOnly: true,
              }}
            />
            <TextField
              id="bmr-goal"
              label={`Goal Calories (${
                weightChange >= 0 ? '+' : ''
              } ${weightChange} calories) `}
              variant="outlined"
              value={`${BMRGoal} cal`}
              InputProps={{
                readOnly: true,
              }}
            />
          </Box>

          <Paper elevation={3} style={{ marginTop: '20px', padding: '20px' }}>
            <Grid container>
              <Grid item xs={12}>
                <FormControl component="fieldset">
                  <FormLabel component="legend">
                    Customization Options
                  </FormLabel>
                  <RadioGroup
                    row
                    aria-label="options"
                    name="row-radio-buttons-group"
                  >
                    <FormControlLabel
                      value="balanced"
                      control={<Radio />}
                      label="Balanced"
                      onClick={() => {
                        setMacroBreakdown({
                          carbs: 0.45,
                          protien: 0.3,
                          fat: 0.25,
                        });
                        calcMacros(BMRGoal, {
                          carbs: 0.45,
                          protien: 0.3,
                          fat: 0.25,
                        });
                        setModalContext('');
                      }}
                    />
                    <FormControlLabel
                      value="high-carb"
                      control={<Radio />}
                      label="High Carb"
                      onClick={() => {
                        setMacroBreakdown({
                          carbs: 0.5,
                          protien: 0.3,
                          fat: 0.2,
                        });
                        calcMacros(BMRGoal, {
                          carbs: 0.5,
                          protien: 0.3,
                          fat: 0.2,
                        });
                        setModalContext('');
                      }}
                    />
                    <FormControlLabel
                      value="low-carb"
                      control={<Radio />}
                      label="Low Carb"
                      onClick={() => {
                        setMacroBreakdown({
                          carbs: 0.4,
                          protien: 0.3,
                          fat: 0.3,
                        });
                        calcMacros(BMRGoal, {
                          carbs: 0.4,
                          protien: 0.3,
                          fat: 0.3,
                        });
                        setModalContext('');
                      }}
                    />
                    <FormControlLabel
                      value="cut"
                      control={<Radio />}
                      label="BodyBuilder Cut"
                      onClick={() => {
                        handleOpen('cut');
                      }}
                    />
                    <FormControlLabel
                      value="bulk"
                      control={<Radio />}
                      label="BodyBuilder Clean Bulk"
                      onClick={() => {
                        handleOpen('bulk');
                      }}
                    />
                  </RadioGroup>
                </FormControl>
              </Grid>
              <Grid item xs={7}>
                <Graph macroBreakdown={macroBreakdown} />
              </Grid>
              <Grid
                item
                xs={1}
                style={{
                  display: 'flex',
                  justifyContent: 'right',
                  paddingRight: '20px',
                  flexDirection: 'column',
                }}
              >
                <Box
                  marginTop="125px"
                  width="25px"
                  height="25px"
                  backgroundColor="#FDCA40"
                />

                <Box
                  marginTop="55px"
                  width="25px"
                  height="25px"
                  backgroundColor="#61210F"
                />

                <Box
                  marginTop="55px"
                  width="25px"
                  height="25px"
                  backgroundColor="#3066BE"
                />
              </Grid>
              <Grid item xs={4}>
                <Box
                  component="form"
                  sx={{
                    '& > :not(style)': { m: 1.5, width: '15ch' },
                  }}
                  noValidate
                  autoComplete="off"
                  style={{ paddingTop: '100px' }}
                >
                  <TextField
                    id="carbs"
                    label="Carbs"
                    variant="outlined"
                    value={`${carbs} g`}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    id="carbs-percent"
                    label=""
                    variant="outlined"
                    value={`${Math.round(
                      Math.round(macroBreakdown.carbs * 100)
                    )}%`}
                    InputProps={{
                      readOnly: true,
                    }}
                  />

                  <TextField
                    id="protien"
                    label="Protien"
                    variant="outlined"
                    value={`${protien} g`}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    id="protien-percent"
                    label=""
                    variant="outlined"
                    value={`${Math.round(macroBreakdown.protien * 100)}%`}
                    InputProps={{
                      readOnly: true,
                    }}
                  />

                  <TextField
                    id="fat"
                    label="Fat"
                    variant="outlined"
                    value={`${fat} g`}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    id="fat-percent"
                    label=""
                    variant="outlined"
                    value={`${macroBreakdown.fat * 100}%`}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                </Box>
              </Grid>
            </Grid>
          </Paper>
        </Box>
      )}
    </Box>
  );
};

export default OutputForm;
