import React, { useState } from 'react';
import {
  Box,
  Container,
  Button,
  Grid,
  Paper,
  Typography,
  TextField,
  MenuItem,
} from '@mui/material';
import { makeStyles } from '@material-ui/core';

import { activityLevelOptions, weightChangeOptions } from './Assets/options';

import Hero from '../../Hero/Hero';
import Footer from '../../Footer/Footer';

const MacroCalculator = (props) => {
  const [weight, setWeight] = React.useState('');
  const [heightFt, setHeightFt] = React.useState('');
  const [heightIn, setHeightIn] = React.useState('');
  const [age, setAge] = React.useState('');
  const [activity, setActivity] = React.useState('');
  const [weightChange, setWeightChange] = React.useState('');

  const [BMR, setBMR] = React.useState('');
  const [BMRAdjusted, setBMRAdjusted] = React.useState('');

  const [submitted, setSubmitted] = React.useState(false);

  const onSubmit = () => {
    calcBMR();
    setSubmitted(true);
  };

  const calcBMR = () => {
    const bmr = Math.ciel(
      66.47 + 6.24 * weight + 12.47 * (heightFt * 12 + heightIn) - 6.755 * age
    );

    const bmrAdjusted = bmr * activity;

    setBMR(bmr);
    setBMRAdjusted(bmrAdjusted);
  };

  return (
    <Box>
      <Container maxWidth="lg">
        <h1>Macro Calculator</h1>
        <Box
          component="form"
          sx={{
            '& > :not(style)': { m: 1, width: '40ch' },
          }}
          noValidate
          autoComplete="off"
        >
          <TextField
            id="weight"
            label="Weight (lb)"
            variant="outlined"
            value={weight}
            onChange={(e) => setWeight(parseInt(e.target.value))}
          />
          <TextField
            id="height-ft"
            label="Height (ft)"
            variant="outlined"
            value={heightFt}
            onChange={(e) => setHeightFt(parseInt(e.target.value))}
          />
          <TextField
            id="height-in"
            label="Height (in)"
            variant="outlined"
            value={heightIn}
            onChange={(e) => setHeightIn(parseInt(e.target.value))}
          />
          <TextField
            id="age"
            label="Age"
            variant="outlined"
            value={age}
            onChange={(e) => setAge(parseInt(e.target.value))}
          />

          <TextField
            id="activity-level"
            select
            label="Activity Level"
            value={activity}
            onChange={(e) => setActivity(e.target.value)}
          >
            {activityLevelOptions.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>

          <TextField
            id="weekly-weight-change"
            select
            label="Weekly Weight Change"
            value={weightChange}
            onChange={(e) => setWeightChange(e.target.value)}
          >
            {weightChangeOptions.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>
          <Button onClick={() => onSubmit()} variant="contained">
            Submit
          </Button>
        </Box>

        {submitted && (
          <Box
            component="form"
            sx={{
              '& > :not(style)': { m: 1, width: '40ch' },
            }}
            noValidate
            autoComplete="off"
          >
            <TextField
              id="bmr"
              label="Basal Metabolic Rate (BMR)"
              variant="outlined"
              value={BMR}
            />
          </Box>
        )}
      </Container>
    </Box>
  );
};

export default MacroCalculator;
