import {
  Box,
  Button,
  makeStyles,
  Typography,
  Grid,
  Paper,
  Container,
} from '@material-ui/core';
import React from 'react';

import Navigation from '../Navigation/Navigation';

// import landscape from '../Assets/landscape.jpeg';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: '#666',
    // height: '800px',
  },
  section: {
    height: '75vh',
    // backgroundImage:
    //   ' url(https://images.pexels.com/photos/167699/pexels-photo-167699.jpeg?cs=srgb&dl=pexels-lumn-167699.jpg&fm=jpg)',
    backgroundSize: 'cover',
  },
  container: {
    height: '100%',
    paddingTop: '5vh',
  },
  content: {
    height: '100%',
    justifyContent: 'center',
    alignItems: 'center',
    // transform: 'translate(0, -20%)',
    color: 'white',
    position: 'relative',
    zIndex: 50,
  },
  heading: {
    textAlign: 'center',
    marginBottom: '30px',
  },
  caption: {
    textAlign: 'center',
  },
  nav: {
    position: 'relative',
    zIndex: 100,
  },
  overlay: {
    backgroundColor: 'rgba(33, 33, 33, 0.2)',
    height: '75vh',
    width: '100%',
    position: 'absolute',
    zIndex: 2,
  },
  cta: {
    marginTop: '100px',
    width: '400px',
    height: '50px',
  },
}));

const Hero = ({ heading, caption, image }) => {
  const classes = useStyles();

  return (
    <Paper
      className={classes.section}
      style={{ backgroundImage: `url(${image})` }}
    >
      <div className={classes.overlay}></div>
      <Container className={classes.container} maxWidth="sm">
        <div className={classes.nav}>
          <Navigation color="white" />
        </div>
        <Grid container xs={12} className={classes.content}>
          <Grid item xs={12}>
            <Box className={classes.heading}>
              <Typography variant="h1">Bryce Lewis</Typography>
            </Box>
            <Box className={classes.caption}>
              <Typography variant="h5">Bryce Lewis</Typography>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Paper>
  );
};

export default Hero;
