import React, { useState } from 'react';
import {
  Box,
  Container,
  Button,
  Grid,
  Paper,
  Typography,
  TextField,
  MenuItem,
} from '@mui/material';
import { makeStyles } from '@material-ui/core';

import { activityLevelOptions, weightChangeOptions } from './Assets/options';

import Hero from '../../Hero/Hero';
import Footer from '../../Footer/Footer';

const MacroCalculator = (props) => {
  const [weight, setWeight] = React.useState('');
  const [heightFt, setHeightFt] = React.useState('');
  const [heightIn, setHeightIn] = React.useState('');
  const [age, setAge] = React.useState('');
  const [activity, setActivity] = React.useState('');
  const [weightChange, setWeightChange] = React.useState('');

  const [BMR, setBMR] = React.useState('');
  const [BMRAdjusted, setBMRAdjusted] = React.useState('');

  const print = () => {
    calcBMR();
  };

  const calcBMR = () => {
    const bmr =
      66.47 + 6.24 * weight + 12.47 * (heightFt * 12 + heightIn) - 6.755 * age;

    const bmrAdjusted = bmr * activity;

    console.log(`BMR: ${bmr} BMR Adjusted: ${bmrAdjusted}`);

    console.log(
      `heightFt ${heightFt} heightIn ${heightIn} weight ${weight} age ${age} `
    );
  };

  return (
    <Box>
      <Container maxWidth="lg">
        <h1>Macro Calculator</h1>
        <Box
          component="form"
          sx={{
            '& > :not(style)': { m: 1, width: '40ch' },
          }}
          noValidate
          autoComplete="off"
        >
          <TextField
            id="outlined-basic"
            label="Weight (lb)"
            variant="outlined"
            value={weight}
            onChange={(e) => setWeight(e.target.value)}
          />
          <TextField
            id="outlined-basic"
            label="Height (ft)"
            variant="outlined"
            value={heightFt}
            onChange={(e) => setHeightFt(e.target.value)}
          />
          <TextField
            id="outlined-basic"
            label="Height (in)"
            variant="outlined"
            value={heightIn}
            onChange={(e) => setHeightIn(e.target.value)}
          />
          <TextField
            id="outlined-basic"
            label="Age"
            variant="outlined"
            value={age}
            onChange={(e) => setAge(e.target.value)}
          />

          <TextField
            id="activity-level"
            select
            label="Activity Level"
            value={activity}
            onChange={(e) => setActivity(e.target.value)}
          >
            {activityLevelOptions.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>

          <TextField
            id="weekly-weight-change"
            select
            label="Weekly Weight Change"
            value={weightChange}
            onChange={(e) => setWeightChange(e.target.value)}
          >
            {weightChangeOptions.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>
          <Button onClick={() => print()} variant="contained">
            Submit
          </Button>
        </Box>
      </Container>
    </Box>
  );
};

export default MacroCalculator;
