import React from 'react';

import {
  Box,
  Container,
  Button,
  Grid,
  Paper,
  Typography,
  TextField,
  MenuItem,
  FormControl,
  FormLabel,
  FormControlLabel,
  RadioGroup,
  Radio,
} from '@mui/material';
import { makeStyles } from '@material-ui/core';

import {
  XYPlot,
  ArcSeries,
  DiscreteColorLegend,
  HorizontalGridLines,
} from 'react-vis';

const Graph = ({ macroBreakdown }) => {
  const PI = Math.PI;
  const portion = Math.random();

  const myData = [
    {
      angle0: 0,
      angle: macroBreakdown.carbs * PI * 2,
      radius0: 0,
      radius: 200,
      color: '#512D38',
      label: 'carbs',
    },
    {
      angle0: macroBreakdown.carbs * PI * 2,
      angle: macroBreakdown.fat * PI * 2 + macroBreakdown.carbs * PI * 2,
      radius0: 0,
      radius: 200,
      color: '#5BC0EB',
      label: 'fat',
    },
    {
      angle0: macroBreakdown.fat * PI * 2 + macroBreakdown.carbs * PI * 2,
      angle: 2 * PI,
      radius0: 0,
      radius: 200,
      color: '#49A078',
      label: 'protien',
      subLabel: 'protien',
      labelsAboveChildren: true,
      showLabels: true,
    },
  ];

  return (
    <Box>
      <XYPlot
        showLabels={true}
        xDomain={[-5, 5]}
        yDomain={[-5, 5]}
        width={500}
        height={500}
      >
        <ArcSeries
          animation
          showLabels={true}
          radiusType={'literal'}
          center={{ x: 0, y: 0 }}
          data={myData}
          colorType={'literal'}
        />
      </XYPlot>
      {/* <DiscreteColorLegend /> */}
      <p>{`Carbs: ${macroBreakdown.carbs}`}</p>
      <p>{`Protien: ${macroBreakdown.protien}`}</p>
      <p>{`Fat: ${macroBreakdown.fat}`}</p>
    </Box>
  );
};

export default Graph;
