import React, { useState } from 'react';
import {
  Box,
  Container,
  Button,
  Grid,
  Paper,
  Typography,
  TextField,
  MenuItem,
} from '@mui/material';
import { makeStyles } from '@material-ui/core';

import Hero from '../../Hero/Hero';
import Footer from '../../Footer/Footer';

const MacroCalculator = (props) => {
  const [weight, setWeight] = React.useState('');
  const [heightFt, setHeightFt] = React.useState('');
  const [heightIn, setHeightIn] = React.useState('');
  const [age, setAge] = React.useState('');
  const [activity, setActivity] = React.useState('');
  const [weightChange, setWeightChange] = React.useState('');

  const activityLevels = [
    {
      value: 1.1,
      label: 'Sedentary',
    },
    {
      value: 1.2,
      label: 'Moderately Active',
    },
    {
      value: 1.3,
      label: 'Very Active',
    },
  ];

  const weightChangeOptions = [
    {
      label: '- 2 lb',
      value: -1000,
    },
    {
      label: '- 1.5 lb',
      value: -750,
    },
    {
      label: '- 1 lb',
      value: -500,
    },
    {
      label: '- 0.5 lb',
      value: -250,
    },
    {
      label: '+ 0 lb',
      value: 0,
    },
    {
      label: '+ 0.5 lb',
      value: 250,
    },
    {
      label: '+ 1 lb',
      value: 500,
    },
    {
      label: '+ 1.5 lb',
      value: 750,
    },
    {
      label: '+ 2 lb',
      value: 1000,
    },
  ];

  const print = () => {
    console.log(weight, heightFt, heightIn, age, activity);
  };

  return (
    <Box>
      <Container maxWidth="lg">
        <h1>Macro Calculator</h1>
        <Box
          component="form"
          sx={{
            '& > :not(style)': { m: 1, width: '20ch' },
          }}
          noValidate
          autoComplete="off"
        >
          <TextField
            id="outlined-basic"
            label="Weight (lb)"
            variant="outlined"
            value={weight}
            onChange={(e) => setWeight(e.target.value)}
          />
          <TextField
            id="outlined-basic"
            label="Height (ft)"
            variant="outlined"
            value={heightFt}
            onChange={(e) => setHeightFt(e.target.value)}
          />
          <TextField
            id="outlined-basic"
            label="Height (in)"
            variant="outlined"
            value={heightIn}
            onChange={(e) => setHeightIn(e.target.value)}
          />
          <TextField
            id="outlined-basic"
            label="Age"
            variant="outlined"
            value={age}
            onChange={(e) => setAge(e.target.value)}
          />

          <TextField
            id="activity-level"
            select
            label="Activity Level"
            value={activity}
            onChange={(e) => setActivity(e.target.value)}
          >
            {activityLevels.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>

          <TextField
            id="weekly-weight-change"
            select
            label="Weekly Weight Change"
            value={activity}
            onChange={(e) => setActivity(e.target.value)}
          >
            {weightChange.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>
          <Button onClick={() => print()} variant="contained">
            Submit
          </Button>
        </Box>
      </Container>
    </Box>
  );
};

export default MacroCalculator;
