import React, { useState } from 'react';
import {
  Box,
  Container,
  Button,
  Grid,
  Paper,
  Typography,
  TextField,
  MenuItem,
  FormControl,
  FormLabel,
  FormControlLabel,
  RadioGroup,
  Radio,
} from '@mui/material';
import { makeStyles } from '@material-ui/core';

import { activityLevelOptions, weightChangeOptions } from '../Assets/options';

const OutputForm = ({
  weightChange,
  submitted,
  BMR,
  BMRAdjusted,
  BMRGoal,
  protien,
  carbs,
  fat,
  macroBreakdown,
  setMacroBreakdown,
  calcMacros,
}) => {
  return (
    <Box>
      {submitted && (
        <Box>
          <Box
            component="form"
            sx={{
              '& > :not(style)': { m: 1, width: '40ch' },
            }}
            noValidate
            autoComplete="off"
            style={{ paddingTop: '20px' }}
          >
            <TextField
              id="bmr"
              label="Basal Metabolic Rate (BMR)"
              variant="outlined"
              value={BMR}
              InputProps={{
                readOnly: true,
              }}
            />
            <TextField
              id="bmr-adjusted"
              label="BMR Adjusted"
              variant="outlined"
              value={BMRAdjusted}
              InputProps={{
                readOnly: true,
              }}
            />
            <TextField
              id="bmr-goal"
              label={`Goal Calories (${
                weightChange >= 0 ? '+' : ''
              } ${weightChange} calories) `}
              variant="outlined"
              value={BMRGoal}
              InputProps={{
                readOnly: true,
              }}
            />
          </Box>

          <Paper elevation={3} style={{ marginTop: '20px', padding: '20px' }}>
            <Grid container>
              <Grid item xs={12}>
                <FormControl component="fieldset">
                  <FormLabel component="legend">
                    Customization Options
                  </FormLabel>
                  <RadioGroup
                    row
                    aria-label="options"
                    name="row-radio-buttons-group"
                  >
                    <FormControlLabel
                      value="balanced"
                      control={<Radio />}
                      label="Balanced"
                      onClick={() => {
                        setMacroBreakdown({
                          carbs: 0.45,
                          protien: 0.3,
                          fat: 0.25,
                        });
                        calcMacros(BMRGoal, {
                          carbs: 0.45,
                          protien: 0.3,
                          fat: 0.25,
                        });
                      }}
                    />
                    <FormControlLabel
                      value="high-carb"
                      control={<Radio />}
                      label="High Carb"
                      onClick={() => {
                        setMacroBreakdown({
                          carbs: 0.5,
                          protien: 0.3,
                          fat: 0.2,
                        });
                        calcMacros(BMRGoal, {
                          carbs: 0.5,
                          protien: 0.3,
                          fat: 0.2,
                        });
                      }}
                    />
                    <FormControlLabel
                      value="low-carb"
                      control={<Radio />}
                      label="Low Carb"
                      onClick={() => {
                        setMacroBreakdown({
                          carbs: 0.4,
                          protien: 0.3,
                          fat: 0.3,
                        });
                        calcMacros(BMRGoal, {
                          carbs: 0.4,
                          protien: 0.3,
                          fat: 0.3,
                        });
                      }}
                    />
                    <FormControlLabel
                      value="cut"
                      control={<Radio />}
                      label="BodyBuilder Cut"
                      onClick={() => {
                        setMacroBreakdown({
                          carbs: 0.42,
                          protien: 0.38,
                          fat: 0.2,
                        });
                        calcMacros(BMRGoal, {
                          carbs: 0.42,
                          protien: 0.38,
                          fat: 0.2,
                        });
                      }}
                    />
                    <FormControlLabel
                      value="bulk"
                      control={<Radio />}
                      label="BodyBuilder Clean Bulk"
                      onClick={() => {
                        setMacroBreakdown({
                          carbs: 0.42,
                          protien: 0.28,
                          fat: 0.3,
                        });
                        calcMacros(BMRGoal, {
                          carbs: 0.42,
                          protien: 0.28,
                          fat: 0.3,
                        });
                      }}
                    />
                  </RadioGroup>
                </FormControl>
              </Grid>
              <Grid item xs={8}>
                <h1>Graph Placeholder</h1>
              </Grid>
              <Grid item xs={4}>
                <Box
                  component="form"
                  sx={{
                    '& > :not(style)': { m: 1, width: '15ch' },
                  }}
                  noValidate
                  autoComplete="off"
                  style={{ paddingTop: '15px' }}
                >
                  <TextField
                    id="carbs"
                    label="Carbs (g)"
                    variant="outlined"
                    value={carbs}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    id="carbs-percent"
                    label="Carbs %"
                    variant="outlined"
                    value={Math.round(Math.round(macroBreakdown.carbs * 100))}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    id="protien"
                    label="Protien (g)"
                    variant="outlined"
                    value={protien}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    id="protien-percent"
                    label="Protien %"
                    variant="outlined"
                    value={Math.round(macroBreakdown.protien * 100)}
                    InputProps={{
                      readOnly: true,
                    }}
                  />

                  <TextField
                    id="fat"
                    label="Fat (g)"
                    variant="outlined"
                    value={fat}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    id="fat-percent"
                    label="Fat %"
                    variant="outlined"
                    value={macroBreakdown.fat * 100}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                </Box>
              </Grid>
            </Grid>
          </Paper>
        </Box>
      )}
    </Box>
  );
};

export default OutputForm;
