import React, { useState } from 'react';
import {
  Box,
  Container,
  Button,
  Grid,
  Paper,
  Typography,
  TextField,
  MenuItem,
  FormControl,
  FormLabel,
  FormControlLabel,
  RadioGroup,
  Radio,
} from '@mui/material';
import { makeStyles } from '@material-ui/core';

import { activityLevelOptions, weightChangeOptions } from '../Assets/options';

const InputForm = ({
  weight,
  setWeight,
  heightFt,
  setHeightFt,
  heightIn,
  setHeightIn,
  age,
  setAge,
  activity,
  setActivity,
  weightChange,
  setWeightChange,
  submitted,
  onSubmit,
}) => {
  return (
    <Box
      component="form"
      sx={{
        '& > :not(style)': { m: 1, width: '40ch' },
      }}
      noValidate
      autoComplete="off"
    >
      <TextField
        id="weight"
        label="Weight (lb)"
        variant="outlined"
        value={weight}
        onChange={(e) => {
          setWeight(parseInt(e.target.value));
          if (submitted) {
            onSubmit();
          }
        }}
      />
      <TextField
        id="height-ft"
        label="Height (ft)"
        variant="outlined"
        value={heightFt}
        onChange={(e) => {
          setHeightFt(parseInt(e.target.value));
          if (submitted) {
            onSubmit();
          }
        }}
      />
      <TextField
        id="height-in"
        label="Height (in)"
        variant="outlined"
        value={heightIn}
        onChange={(e) => {
          setHeightIn(parseInt(e.target.value));
          if (submitted) {
            onSubmit();
          }
        }}
      />
      <TextField
        id="age"
        label="Age"
        variant="outlined"
        value={age}
        onChange={(e) => {
          setAge(parseInt(e.target.value));
          if (submitted) {
            onSubmit();
          }
        }}
      />

      <TextField
        id="activity-level"
        select
        label="Activity Level"
        value={activity}
        onChange={(e) => {
          setActivity(e.target.value);
          if (submitted) {
            onSubmit();
          }
        }}
      >
        {activityLevelOptions.map((option) => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>

      <TextField
        id="weekly-weight-change"
        select
        label="Weekly Weight Change"
        value={weightChange}
        onChange={(e) => {
          setWeightChange(e.target.value);
          if (submitted) {
            setTimeout(() => {
              onSubmit();
            }, 200);
          }
        }}
      >
        {weightChangeOptions.map((option) => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>

      <Button onClick={() => onSubmit()} variant="contained">
        Submit
      </Button>
    </Box>
  );
};

export default InputForm;
